import 'package:flutter/material.dart';


class CatalogScreen extends StatefulWidget {
  final String userName;

  const CatalogScreen({super.key, required this.userName});

  @override
  _CatalogScreenState createState() => _CatalogScreenState();
}

class _CatalogScreenState extends State<CatalogScreen> {
  final List<String> categories = [
    'Видеокарты',
    'Процессоры',
    'Мат.Платы',
    'ОЗУ',
    'Корпусы',
    'Блоки пит.',
    'Кулеры',
    'Периферия'
  ];

  final List<Map<String, dynamic>> pcComponents = [
    {
      'name': 'NVIDIA RTX 3080',
      'category': 'Видеокарты',
      'price': 89999,
      'image': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRG3TpD4xWlHe9vWnan562_a47RxaD5GWH_znLn3tB0qIdq7QIm-AbZv7oT6GjJOtHkk94&usqp=CAU',
    },
    {
      'name': 'AMD Ryzen 7 5800X',
      'category': 'Процессоры',
      'price': 34999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Intel Core i9-12900K',
      'category': 'Процессоры',
      'price': 49999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'ASUS ROG STRIX B550-F',
      'category': 'Мат.Платы',
      'price': 15999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Kingston Fury 16GB DDR4',
      'category': 'ОЗУ',
      'price': 7999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Corsair 4000D Airflow',
      'category': 'Корпусы',
      'price': 8999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Seasonic Focus GX-750',
      'category': 'Блоки пит.',
      'price': 10999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Noctua NH-D15',
      'category': 'Кулеры',
      'price': 8999,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'Logitech G502 HERO',
      'category': 'Периферия',
      'price': 5499,
      'image': 'data:image/jpeg;base64,',
    },
    {
      'name': 'SteelSeries Apex Pro',
      'category': 'Периферия',
      'price': 19999,
      'image': 'data:image/jpeg;base64,',
    },
  ];

  // Строка для поиска
  String searchQuery = '';
  String selectedCategory = 'Видеокарты';

  // Фильтрация товаров по названию и категории
  List<Map<String, dynamic>> get filteredProducts {
    List<Map<String, dynamic>> filtered = pcComponents;

    // Фильтрация по тексту
    if (searchQuery.isNotEmpty) {
      filtered = filtered
          .where((product) => product['name']
          .toLowerCase()
          .contains(searchQuery.toLowerCase()))
          .toList();
    }

    // Фильтрация по категории
    if (selectedCategory != 'Все категории') {
      filtered = filtered
          .where((product) => product['category'] == selectedCategory)
          .toList();
    }

    return filtered;
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Каталог'),
      ),
      body: CustomScrollView(
        slivers: [
          // Включаем поиск и аватар пользователя в заголовок
          SliverPadding(
            padding: const EdgeInsets.only(top: 50.0, left: 16.0, right: 16.0),
            sliver: SliverList(
              delegate: SliverChildListDelegate([
                Row(
                  children: [
                    Expanded(
                      child: Container(
                        decoration: BoxDecoration(
                          color: Colors.white,
                          borderRadius: BorderRadius.circular(30),
                          boxShadow: [
                            BoxShadow(
                              color: Colors.black.withOpacity(0.1),
                              spreadRadius: 1,
                              blurRadius: 5,
                            ),
                          ],
                        ),
                        child: TextField(
                          onChanged: (value) {
                            setState(() {
                              searchQuery = value;
                            });
                          },
                          decoration: const InputDecoration(
                            hintText: 'Поиск',
                            prefixIcon: Icon(Icons.search),
                            border: InputBorder.none,
                            contentPadding: EdgeInsets.symmetric(vertical: 12.0),
                          ),
                        ),
                      ),
                    ),
                    const SizedBox(width: 10),
                    Column(
                      children: [
                        const CircleAvatar(
                          backgroundImage: AssetImage('assets/user_avatar.png'),
                          radius: 20,
                        ),
                        Text(
                          widget.userName,
                          style: const TextStyle(fontSize: 12),
                        ),
                      ],
                    ),
                  ],
                ),
              ]),
            ),
          ),
          // Сетка категорий
          SliverPadding(
            padding: const EdgeInsets.all(10.0),
            sliver: SliverGrid(
              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 4, // Количество столбцов
                crossAxisSpacing: 20.0,
                mainAxisSpacing: 20.0,
                childAspectRatio: 0.9,
              ),
              delegate: SliverChildBuilderDelegate(
                    (BuildContext context, int index) {
                  return GestureDetector(
                    onTap: () {
                      setState(() {
                        selectedCategory = categories[index];
                      });
                    },
                    child: CategoryItem(
                      categoryName: categories[index],
                      isSelected: selectedCategory == categories[index],
                    ),
                  );
                },
                childCount: categories.length,
              ),
            ),
          ),
          // Сетка отфильтрованных товаров
          SliverPadding(
            padding: const EdgeInsets.all(10.0),
            sliver: SliverGrid(
              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                crossAxisSpacing: 10.0,
                mainAxisSpacing: 10.0,
                childAspectRatio: 0.75,
              ),
              delegate: SliverChildBuilderDelegate(
                    (BuildContext context, int index) {
                  return ProductItem(
                    productName: filteredProducts[index]['name'],
                    productImage: filteredProducts[index]['image'],
                    productPrice: filteredProducts[index]['price'],
                  );
                },
                childCount: filteredProducts.length,
              ),
            ),
          ),
        ],
      ),
      bottomNavigationBar: BottomNavigationBar(
        type: BottomNavigationBarType.fixed,
        currentIndex: 1,
        backgroundColor: Colors.orange,
        selectedItemColor: Colors.grey,
        unselectedItemColor: Colors.white,
        items: const [
          BottomNavigationBarItem(
            icon: Icon(Icons.favorite),
            label: 'Лайки',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.shopping_basket),
            label: 'Каталог',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.support_agent),
            label: 'Поддержка',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.person),
            label: 'Профиль',
          ),
        ],
      ),
    );
  }
}

class CategoryItem extends StatelessWidget {
  final String categoryName;
  final bool isSelected;

  const CategoryItem({super.key, required this.categoryName, required this.isSelected});

  @override
  Widget build(BuildContext context) {
    return Container(
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(12),
        gradient: isSelected
            ? const LinearGradient(
          colors: [Color(0xFFFFC164), Color(0xFFF22E63)],
          begin: Alignment.topLeft,
          end: Alignment.bottomRight,
        )
            : const LinearGradient(
          colors: [Color(0xFFF22E63), Color(0xFFFFC164)],
          begin: Alignment.topLeft,
          end: Alignment.bottomRight,
        ),
      ),
      child: Center(
        child: Text(
          categoryName,
          style: TextStyle(
            fontSize: 16,
            fontWeight: FontWeight.bold,
            color: isSelected ? Colors.white : Colors.black,
          ),
        ),
      ),
    );
  }
}

class ProductItem extends StatelessWidget {
  final String productName;
  final String productImage;
  final double productPrice;

  const ProductItem({
    super.key,
    required this.productName,
    required this.productImage,
    required this.productPrice,
  });

  @override
  Widget build(BuildContext context) {
    return Card(
      elevation: 4,
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(12),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          ClipRRect(
            borderRadius: BorderRadius.circular(12),
            child: Image.network(
              productImage,
              height: 270,
              width: double.infinity,
              fit: BoxFit.cover,
            ),
          ),
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Text(
              productName,
              style: const TextStyle(fontWeight: FontWeight.bold),
            ),
          ),
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: Text('\$${productPrice.toStringAsFixed(2)}'),
          ),
        ],
      ),
    );
  }
}